export default class Timer {
	private dt: number = 0;
	private start: number = 0;
	private now: number = 0;

	constructor() {
		this.start = performance.now();
		this.now = performance.now();
	}

	total() {
		return this.now - this.start;
	}

	delta() {
		return this.dt / 1000;
	}

	step() {
		let now = performance.now();
		let dt = now - this.now;
		this.now = now;
		this.dt = dt;

		return dt;
	}
}