import {ActionType} from "./Action";

let ActionMap = new Map<string, ActionType>();

ActionMap.set("w", ActionType.Forward);
ActionMap.set("s", ActionType.Backward);
ActionMap.set("a", ActionType.Left);
ActionMap.set("d", ActionType.Right);

let InverseActionMap = new Map<ActionType, string[]>();
ActionMap.forEach((value, key) => {
	let list = InverseActionMap.get(value);

	if (!list) {
		list = [];
		InverseActionMap.set(value, list);
	}

	list.push(key);
});

export {InverseActionMap};
export default ActionMap;