let KeyMap = new Map<number, string>();
let InverseKeyMap = new Map<string, number>();

for (let i = 65; i <= 90; i++) {
	KeyMap.set(i, String.fromCharCode(i).toLowerCase());
}

KeyMap.set(32, "space");

KeyMap.forEach((value, key) => {
	InverseKeyMap.set(value, key);
});

export {InverseKeyMap};
export default KeyMap;