import Shader, {ShaderType} from "@client/graphics/Shader";

let shader = new Shader(ShaderType.Fragment, `
#extension GL_EXT_draw_buffers : require
precision highp float;

uniform vec3 color;
uniform sampler2D tex;

varying vec3 fs_normal;
varying vec3 fs_pos;
varying vec2 fs_texcoord;

void main(void) {
	// normal
	gl_FragData[0] = vec4(normalize(fs_normal), 1.0);

	// position
	gl_FragData[1] = vec4(fs_pos, 1.0);

	// color
	gl_FragData[2] = vec4(texture2D(tex, fs_texcoord).xyz, 1.0);
}
`);

export default shader;