import Shader, {ShaderType} from "@client/graphics/Shader";

let shader = new Shader(ShaderType.Vertex, `
precision highp float;

attribute vec3 position;
attribute vec3 normalVector;
attribute vec2 texcoord;

varying vec3 fs_normal;
varying vec3 fs_pos;
varying vec2 fs_texcoord;
varying float fs_depth;

uniform mat4 transformModel;
uniform mat4 transformView;
uniform mat4 transformProjection;

void main(void) {
	gl_Position = transformProjection * transformView * transformModel * vec4(position, 1.0);

	fs_pos = (transformModel * vec4(position, 1.0)).xyz;
	fs_normal = normalVector;
	fs_texcoord = texcoord;
	fs_depth = (gl_Position.z / gl_Position.w);
}
`);

export default shader;